import React from 'react';
import '../Hero/Hero.scss';
import VideoControllers from '../../components/VideoControllers/VideoControllers'


function HeroFeatured(props) {
  return (
    <section className="Hero">
      <video className="Hero__video" poster={props.featured.image} source src={props.featured.video} type="video/mp4"> </video>
      

        <VideoControllers />



    </section>
  );
}

export default HeroFeatured;
