import React from 'react';
import Header from '../Header/Header';
import UploadNewStatus from '../UploadNewStatus/UploadNewStatus';


class UploadPage extends React.Component () {
  state = {
    statusList: [
      'status two',
      'status one'
    ]
  }

  addNewStatus = (event, status) => {
    if (!!status) {
      // TODO: add a new status if it isn't empty

      this.setState({
        statusList: []
      });
    }
  }
  
  render() {
  return (
      <>
        <Header /> 
        <UploadNewStatus submitHandler={this.addNewStatus} />
              
               
      </>
    );
  }  
  }
  
  export default UploadPage;
