import React from 'react';
import './MainAside.scss';
import videoImg1 from '../../assets/images/video-list-1.jpg';



function ListItem(props) {
    return (
        
        <div>
        <img className="aside__img" src={props.image} />
        <div classname="aside__header">
            <h2 className="aside__newTitle">{props.title}</h2>
            <h3 className="aside__author">{props.author}</h3>
        </div>
        </div>
    )
}



function MainAside(props) {
    let listArray = [
        { image: videoImg1, title: 'Become a Travel Pro In One Wasy Lessson...', author: 'Scotty Cranmer' },
        { image: '../../assets/images/video-list-2.jpg', title: 'Les Houches The Hidden Gem Of The…', author: 'Scotty Cranmer' },
        { image: '../../assets/images/video-list-3.jpg', title: 'Travel Health Useful Medical Information…', author: 'Scotty Cranmer' },
        { image: '../../assets/images/video-list-4.jpg', title: 'Cheap Airline Tickets Great Ways To Save', author: 'Emily Harper' },
        { image: '../../assets/images/video-list-5.jpg', title: 'Take A Romantic Break In A Boutique Hotel', author: 'Ethan Owen' },
        { image: '../../assets/images/video-list-6.jpg', title: 'Choose The Perfect Accommodations', author: 'Lydia Perez' },
        { image: '../../assets/images/video-list-7.jpg', title: 'Cruising Destination Ideas', author: 'Timothy Austin' },
        { image: '../../assets/images/video-list-8.jpg', title: 'Train Travel On Track For Safety', author: 'Scotty Cranmer' }

    ];


    return (
        <section className="aside">
            <div className="aside__container">
                <p className="aside__header">Next Video</p>

                <div className="aside__list">

                    {listArray.map((item, index) => {
                        return <ListItem key={index} image={item.image} title={item.title} author={item.author} />
                    })
                    }

                

            </div>


            </div>
        </section >
    );
}



export default MainAside;
